$(function(){AOS.init({once:!0});$(window).scroll(function(){if($(this).scrollTop()>180){$("#scroll-top").fadeIn('slow')}else{$("#scroll-top").fadeOut('slow')}});$(".owl-carousel").owlCarousel({items:1,mouseDrag:!1,touchDrag:!1,loop:!1,nav:!1,animateIn:'slideInRight'});$(".mockup").click(function(){$("#mockup-image").attr("src",this.dataset.imgPath);$(".mockup-modal").css("display","block")});$(".mockup-modal").find("li").click(function(){$(".mockup-modal").css("display","none")})})